  qpowf = function() {
    var a, b, expo, t, x, y;
    expo = 0;
    p2 = pop();
    p1 = pop();
    if (isplusone(p1) || isZeroAtomOrTensor(p2)) {
      push_integer(1);
      return;
    }
    if (isminusone(p1) && isoneovertwo(p2)) {
      push(imaginaryunit);
      return;
    }
    if (isZeroAtomOrTensor(p1)) {
      if (isnegativenumber(p2)) {
        stop("divide by zero");
      }
      push(zero);
      return;
    }
    if (isplusone(p2)) {
      push(p1);
      return;
    }
    if (isinteger(p2)) {
      push(p2);
      expo = pop_integer();
      if (isNaN(expo)) {
        push_symbol(POWER);
        push(p1);
        push(p2);
        list(3);
        return;
      }
      x = mpow(p1.q.a, Math.abs(expo));
      y = mpow(p1.q.b, Math.abs(expo));
      if (expo < 0) {
        t = x;
        x = y;
        y = t;
        x = makeSignSameAs(x, y);
        y = makePositive(y);
      }
      p3 = new U();
      p3.k = NUM;
      p3.q.a = x;
      p3.q.b = y;
      push(p3);
      return;
    }
    if (isminusone(p1)) {
      push(p2);
      normalize_angle();
      return;
    }
    if (isnegativenumber(p1)) {
      push(p1);
      negate();
      push(p2);
      qpow();
      push_integer(-1);
      push(p2);
      qpow();
      multiply();
      return;
    }
    if (!isinteger(p1)) {
      push(p1);
      mp_numerator();
      push(p2);
      qpow();
      push(p1);
      mp_denominator();
      push(p2);
      negate();
      qpow();
      multiply();
      return;
    }
    if (is_small_integer(p1)) {
      push(p1);
      push(p2);
      quickfactor();
      return;
    }
    if (!isSmall(p2.q.a) || !isSmall(p2.q.b)) {
      push_symbol(POWER);
      push(p1);
      push(p2);
      list(3);
      return;
    }
    a = p2.q.a;
    b = p2.q.b;
    x = mroot(p1.q.a, b);
    if (x === 0) {
      push_symbol(POWER);
      push(p1);
      push(p2);
      list(3);
      return;
    }
    y = mpow(x, a);
    p3 = new U();
    p3.k = NUM;
    if (p2.q.a.isNegative()) {
      p3.q.a = bigInt(1);
      p3.q.b = y;
    } else {
      p3.q.a = y;
      p3.q.b = bigInt(1);
    }
    return push(p3);
  };

